import React, { createContext, useContext, useState, useEffect } from 'react';
import { tripApi } from '../services/api';

interface Trip {
  _id: string;
  destination: string;
  country: string;
  startDate: Date | null;
  endDate: Date | null;
  description: string;
  image: string;
  budget: {
    amount: number;
    currency: string;
  };
  activities: Array<{
    name: string;
    date: Date | null;
    location: string;
    notes: string;
  }>;
  accommodation: {
    name: string;
    address: string;
    checkIn: Date | null;
    checkOut: Date | null;
    bookingReference: string;
  };
  transportation: {
    type: string;
    bookingReference: string;
    departureTime: Date | null;
    arrivalTime: Date | null;
    notes: string;
  };
  status: 'planned' | 'ongoing' | 'completed' | 'cancelled';
}

interface TripContextType {
  trips: Trip[];
  loading: boolean;
  error: string | null;
  fetchTrips: () => Promise<void>;
  getTrip: (id: string) => Promise<Trip>;
  createTrip: (tripData: Omit<Trip, '_id'>) => Promise<void>;
  updateTrip: (id: string, tripData: Partial<Trip>) => Promise<void>;
  deleteTrip: (id: string) => Promise<void>;
}

const TripContext = createContext<TripContextType | undefined>(undefined);

export const TripProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [trips, setTrips] = useState<Trip[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchTrips = async () => {
    try {
      setLoading(true);
      const response = await tripApi.getTrips();
      setTrips(response.data);
      setError(null);
    } catch (err: any) {
      const errorMessage = err.response?.data?.message || err.message || 'Failed to fetch trips';
      setError(`Error: ${errorMessage}. Please ensure the server is running on port 5001 and MongoDB is connected.`);
      console.error('Error fetching trips:', err);
    } finally {
      setLoading(false);
    }
  };

  const getTrip = async (id: string): Promise<Trip> => {
    try {
      setLoading(true);
      const response = await tripApi.getTrip(id);
      return response.data;
    } catch (err) {
      setError('Failed to fetch trip');
      console.error('Error fetching trip:', err);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const createTrip = async (tripData: Omit<Trip, '_id'>) => {
    try {
      setLoading(true);
      const response = await tripApi.createTrip(tripData);
      setTrips(prev => [...prev, response.data]);
      setError(null);
    } catch (err: any) {
      const errorMessage = err.response?.data?.message || err.message || 'Failed to create trip';
      setError(`Error: ${errorMessage}. Please ensure the server is running and all required fields are filled.`);
      console.error('Error creating trip:', err);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const updateTrip = async (id: string, tripData: Partial<Trip>) => {
    try {
      setLoading(true);
      const response = await tripApi.updateTrip(id, tripData);
      setTrips(prev => prev.map(trip => 
        trip._id === id ? response.data : trip
      ));
      setError(null);
    } catch (err) {
      setError('Failed to update trip');
      console.error('Error updating trip:', err);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const deleteTrip = async (id: string) => {
    try {
      setLoading(true);
      await tripApi.deleteTrip(id);
      setTrips(prev => prev.filter(trip => trip._id !== id));
      setError(null);
    } catch (err) {
      setError('Failed to delete trip');
      console.error('Error deleting trip:', err);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTrips();
  }, []);

  useEffect(() => {
    // For development: Add sample trips if none exist
    const addSampleTrips = async () => {
      if (trips.length === 0) {
        const sampleTrips: Omit<Trip, '_id'>[] = [
          {
            destination: 'Paris',
            country: 'France',
            startDate: new Date('2024-06-15'),
            endDate: new Date('2024-06-22'),
            description: 'Experience the magic of Paris with visits to the Eiffel Tower, Louvre Museum, and charming cafes along the Seine River.',
            image: 'https://images.unsplash.com/photo-1499856871958-5b9627545d1a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
            budget: {
              amount: 3000,
              currency: 'EUR'
            },
            activities: [
              {
                name: 'Eiffel Tower Visit',
                date: new Date('2024-06-16'),
                location: 'Champ de Mars',
                notes: 'Book sunset time slot for best views'
              },
              {
                name: 'Louvre Museum Tour',
                date: new Date('2024-06-17'),
                location: 'Rue de Rivoli',
                notes: 'Get there early to see Mona Lisa without crowds'
              }
            ],
            accommodation: {
              name: 'Le Petit Palace',
              address: '123 Rue Saint-Honoré',
              checkIn: new Date('2024-06-15'),
              checkOut: new Date('2024-06-22'),
              bookingReference: 'PARIS2024'
            },
            transportation: {
              type: 'Flight',
              bookingReference: 'AF1234',
              departureTime: new Date('2024-06-15T10:00:00'),
              arrivalTime: new Date('2024-06-15T14:00:00'),
              notes: 'Air France, Terminal 2E'
            },
            status: 'planned' as const
          },
          {
            destination: 'Tokyo',
            country: 'Japan',
            startDate: new Date('2024-09-10'),
            endDate: new Date('2024-09-20'),
            description: 'Immerse in Japanese culture with visits to ancient temples, modern districts, and world-class restaurants.',
            image: 'https://images.unsplash.com/photo-1536098561742-ca998e48cbcc?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
            budget: {
              amount: 400000,
              currency: 'JPY'
            },
            activities: [
              {
                name: 'Senso-ji Temple Visit',
                date: new Date('2024-09-11'),
                location: 'Asakusa',
                notes: 'Early morning to avoid crowds'
              },
              {
                name: 'Tsukiji Fish Market Tour',
                date: new Date('2024-09-12'),
                location: 'Tsukiji',
                notes: 'Book sushi making workshop'
              }
            ],
            accommodation: {
              name: 'Shinjuku Grand Hotel',
              address: '1-1 Shinjuku',
              checkIn: new Date('2024-09-10'),
              checkOut: new Date('2024-09-20'),
              bookingReference: 'TYO2024'
            },
            transportation: {
              type: 'Flight',
              bookingReference: 'JL789',
              departureTime: new Date('2024-09-10T08:00:00'),
              arrivalTime: new Date('2024-09-11T10:00:00'),
              notes: 'Japan Airlines, Terminal 1'
            },
            status: 'planned' as const
          },
          {
            destination: 'Bali',
            country: 'Indonesia',
            startDate: new Date('2024-07-01'),
            endDate: new Date('2024-07-10'),
            description: 'Tropical paradise featuring pristine beaches, ancient temples, and lush rice terraces.',
            image: 'https://images.unsplash.com/photo-1537996194471-e657df975ab4?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
            budget: {
              amount: 15000000,
              currency: 'IDR'
            },
            activities: [
              {
                name: 'Ubud Rice Terrace Tour',
                date: new Date('2024-07-02'),
                location: 'Tegalalang',
                notes: 'Bring camera and comfortable shoes'
              },
              {
                name: 'Uluwatu Temple Sunset',
                date: new Date('2024-07-03'),
                location: 'Uluwatu',
                notes: 'Watch Kecak fire dance performance'
              }
            ],
            accommodation: {
              name: 'Ubud Jungle Resort',
              address: 'Jalan Raya Ubud',
              checkIn: new Date('2024-07-01'),
              checkOut: new Date('2024-07-10'),
              bookingReference: 'BALI2024'
            },
            transportation: {
              type: 'Flight',
              bookingReference: 'GA456',
              departureTime: new Date('2024-07-01T06:00:00'),
              arrivalTime: new Date('2024-07-01T14:00:00'),
              notes: 'Garuda Indonesia, Terminal 3'
            },
            status: 'planned' as const
          }
        ];

        try {
          // Create each sample trip in the database
          for (const trip of sampleTrips) {
            await createTrip(trip);
          }
        } catch (error) {
          console.error('Error adding sample trips:', error);
        }
      }
    };

    addSampleTrips();
  }, [trips]);

  const value = {
    trips,
    loading,
    error,
    fetchTrips,
    getTrip,
    createTrip,
    updateTrip,
    deleteTrip,
  };

  return (
    <TripContext.Provider value={value}>
      {children}
    </TripContext.Provider>
  );
};

export const useTrips = () => {
  const context = useContext(TripContext);
  if (context === undefined) {
    throw new Error('useTrips must be used within a TripProvider');
  }
  return context;
};
