import React from 'react';
import { Container, Typography, Button, Grid, Card, CardContent, CardMedia, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import ExploreIcon from '@mui/icons-material/Explore';
import AddIcon from '@mui/icons-material/Add';

const featuredDestinations = [
  {
    title: 'Paris, France',
    image: 'https://source.unsplash.com/800x600/?paris',
    description: 'Experience the city of love and its iconic landmarks.',
  },
  {
    title: 'Tokyo, Japan',
    image: 'https://source.unsplash.com/800x600/?tokyo',
    description: 'Immerse yourself in Japanese culture and modern technology.',
  },
  {
    title: 'New York, USA',
    image: 'https://source.unsplash.com/800x600/?newyork',
    description: 'Explore the city that never sleeps.',
  },
];

const Home = () => {
  const navigate = useNavigate();

  return (
    <Container maxWidth="lg">
      {/* Hero Section */}
      <Box sx={{ textAlign: 'center', py: 8 }}>
        <Typography variant="h2" component="h1" gutterBottom>
          Plan Your Next Adventure
        </Typography>
        <Typography variant="h5" color="text.secondary" paragraph>
          Discover amazing destinations and create unforgettable memories
        </Typography>
        <Box sx={{ mt: 4, display: 'flex', gap: 2, justifyContent: 'center' }}>
          <Button
            variant="contained"
            size="large"
            startIcon={<ExploreIcon />}
            onClick={() => navigate('/trips')}
          >
            Explore Trips
          </Button>
          <Button
            variant="outlined"
            size="large"
            startIcon={<AddIcon />}
            onClick={() => navigate('/planner')}
          >
            Create New Trip
          </Button>
        </Box>
      </Box>

      {/* Featured Destinations */}
      <Box sx={{ my: 8 }}>
        <Typography variant="h4" component="h2" gutterBottom sx={{ mb: 4 }}>
          Featured Destinations
        </Typography>
        <Grid container spacing={4}>
          {featuredDestinations.map((destination) => (
            <Grid item xs={12} md={4} key={destination.title}>
              <Card sx={{ height: '100%' }}>
                <CardMedia
                  component="img"
                  height="200"
                  image={destination.image}
                  alt={destination.title}
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="h3">
                    {destination.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {destination.description}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* Call to Action */}
      <Box sx={{ textAlign: 'center', py: 8, bgcolor: 'primary.light', borderRadius: 2, color: 'white', mb: 8 }}>
        <Typography variant="h4" component="h2" gutterBottom>
          Ready to Start Planning?
        </Typography>
        <Typography variant="body1" paragraph>
          Create your personalized travel itinerary today
        </Typography>
        <Button
          variant="contained"
          size="large"
          color="secondary"
          onClick={() => navigate('/planner')}
        >
          Start Planning
        </Button>
      </Box>
    </Container>
  );
};

export default Home;
