import React, { useState } from 'react';
import {
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  Box,
  Chip,
  Rating,
  useTheme,
  useMediaQuery,
  Card,
  CardContent,
  CardMedia,
  Grid,
} from '@mui/material';
import {
  LocationOn,
  AccessTime,
  AttachMoney,
  Language,
} from '@mui/icons-material';

interface Destination {
  id: number;
  name: string;
  country: string;
  image: string;
  description: string;
  bestTimeToVisit: string;
  averageCost: string;
  rating: number;
  language: string;
  attractions: string[];
  currency: string;
}

const destinations: Destination[] = [
  {
    id: 1,
    name: 'Santorini',
    country: 'Greece',
    image: 'https://images.unsplash.com/photo-1613395877344-13d4a8e0d49e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
    description: 'Famous for its stunning white-washed buildings, blue-domed churches, and spectacular sunsets over the Aegean Sea.',
    bestTimeToVisit: 'April to October',
    averageCost: '$150-200 per day',
    rating: 4.8,
    language: 'Greek',
    attractions: ['Oia Village', 'Fira', 'Red Beach', 'Ancient Thera'],
    currency: 'Euro (EUR)',
  },
  {
    id: 2,
    name: 'Kyoto',
    country: 'Japan',
    image: 'https://images.unsplash.com/photo-1493976040374-85c8e12f0c0e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
    description: 'Historic city known for its classical Buddhist temples, gardens, imperial palaces, and traditional wooden houses.',
    bestTimeToVisit: 'March-May and October-November',
    averageCost: '$130-180 per day',
    rating: 4.7,
    language: 'Japanese',
    attractions: ['Kinkaku-ji', 'Fushimi Inari Shrine', 'Arashiyama Bamboo Grove'],
    currency: 'Japanese Yen (JPY)',
  },
  {
    id: 3,
    name: 'Machu Picchu',
    country: 'Peru',
    image: 'https://images.unsplash.com/photo-1526392060635-9d6019884377?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
    description: 'Ancient Incan city set high in the Andes Mountains, featuring incredible stone architecture and breathtaking mountain views.',
    bestTimeToVisit: 'April to October',
    averageCost: '$120-170 per day',
    rating: 4.9,
    language: 'Spanish',
    attractions: ['Sun Gate', 'Huayna Picchu', 'Temple of the Sun'],
    currency: 'Peruvian Sol (PEN)',
  },
  {
    id: 4,
    name: 'Dubai',
    country: 'UAE',
    image: 'https://images.unsplash.com/photo-1512453979798-5ea266f8880c?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
    description: 'Ultra-modern city known for luxury shopping, futuristic architecture, and vibrant nightlife.',
    bestTimeToVisit: 'November to March',
    averageCost: '$200-300 per day',
    rating: 4.6,
    language: 'Arabic',
    attractions: ['Burj Khalifa', 'Dubai Mall', 'Palm Jumeirah', 'Desert Safari'],
    currency: 'UAE Dirham (AED)',
  },
  {
    id: 5,
    name: 'Amalfi Coast',
    country: 'Italy',
    image: 'https://images.unsplash.com/photo-1533587851505-d119e13fa0d7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
    description: 'Stunning stretch of coastline known for its picturesque towns, dramatic cliffs, and crystal-clear waters.',
    bestTimeToVisit: 'May to October',
    averageCost: '$170-250 per day',
    rating: 4.7,
    language: 'Italian',
    attractions: ['Positano', 'Ravello', 'Path of the Gods', 'Amalfi Cathedral'],
    currency: 'Euro (EUR)',
  },
];

const Trips = () => {
  const [selectedDestination, setSelectedDestination] = useState<Destination | null>(null);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const isTablet = useMediaQuery(theme.breakpoints.down('md'));

  const renderMobileView = () => (
    <Grid container spacing={2}>
      {destinations.map((destination) => (
        <Grid item xs={12} key={destination.id}>
          <Card>
            <CardMedia
              component="img"
              height="200"
              image={destination.image}
              alt={destination.name}
              sx={{ objectFit: 'cover' }}
            />
            <CardContent>
              <Typography variant="h6" gutterBottom>
                {destination.name}, {destination.country}
              </Typography>
              <Box sx={{ mb: 1 }}>
                <Rating value={destination.rating} precision={0.1} readOnly size="small" />
              </Box>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Best Time: {destination.bestTimeToVisit}
              </Typography>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Cost: {destination.averageCost}
              </Typography>
              <Button
                variant="contained"
                size="small"
                fullWidth
                sx={{ mt: 1 }}
                onClick={() => setSelectedDestination(destination)}
              >
                View Details
              </Button>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );

  const renderTableView = () => (
    <TableContainer component={Paper} sx={{ mt: 3, mb: 4 }}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Destination</TableCell>
            <TableCell>Country</TableCell>
            {!isTablet && <TableCell>Best Time to Visit</TableCell>}
            <TableCell>Average Cost</TableCell>
            <TableCell>Rating</TableCell>
            <TableCell>Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {destinations.map((destination) => (
            <TableRow key={destination.id} hover>
              <TableCell>{destination.name}</TableCell>
              <TableCell>{destination.country}</TableCell>
              {!isTablet && <TableCell>{destination.bestTimeToVisit}</TableCell>}
              <TableCell>{destination.averageCost}</TableCell>
              <TableCell>
                <Rating value={destination.rating} precision={0.1} readOnly size="small" />
              </TableCell>
              <TableCell>
                <Button
                  variant="contained"
                  size="small"
                  onClick={() => setSelectedDestination(destination)}
                >
                  View Details
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom sx={{ fontSize: { xs: '1.75rem', sm: '2.125rem' } }}>
        Explore Amazing Destinations
      </Typography>

      {isMobile ? renderMobileView() : renderTableView()}

      <Dialog
        open={Boolean(selectedDestination)}
        onClose={() => setSelectedDestination(null)}
        maxWidth="md"
        fullWidth
        scroll="body"
      >
        {selectedDestination && (
          <>
            <DialogTitle>
              <Typography variant="h5" sx={{ fontSize: { xs: '1.25rem', sm: '1.5rem' } }}>
                {selectedDestination.name}, {selectedDestination.country}
              </Typography>
            </DialogTitle>
            <DialogContent>
              <Box sx={{ mb: 3 }}>
                <img
                  src={selectedDestination.image}
                  alt={selectedDestination.name}
                  style={{
                    width: '100%',
                    height: isMobile ? '200px' : '300px',
                    objectFit: 'cover',
                    borderRadius: '8px',
                  }}
                />
              </Box>

              <Typography variant="body1" paragraph>
                {selectedDestination.description}
              </Typography>

              <Box sx={{ 
                display: 'flex', 
                flexDirection: isMobile ? 'column' : 'row',
                gap: 2, 
                mb: 2 
              }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <AccessTime color="action" />
                  <Typography variant="body2">
                    Best Time: {selectedDestination.bestTimeToVisit}
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <AttachMoney color="action" />
                  <Typography variant="body2">
                    {selectedDestination.averageCost}
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Language color="action" />
                  <Typography variant="body2">
                    {selectedDestination.language}
                  </Typography>
                </Box>
              </Box>

              <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
                Popular Attractions
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                {selectedDestination.attractions.map((attraction) => (
                  <Chip
                    key={attraction}
                    label={attraction}
                    icon={<LocationOn />}
                    variant="outlined"
                    sx={{ mb: 1 }}
                  />
                ))}
              </Box>
            </DialogContent>
            <DialogActions sx={{ flexDirection: isMobile ? 'column' : 'row', gap: 1, p: 2 }}>
              <Button 
                onClick={() => setSelectedDestination(null)}
                fullWidth={isMobile}
              >
                Close
              </Button>
              <Button
                variant="contained"
                fullWidth={isMobile}
                onClick={() => {
                  console.log('Plan trip to:', selectedDestination.name);
                }}
              >
                Plan Trip
              </Button>
            </DialogActions>
          </>
        )}
      </Dialog>
    </Container>
  );
};

export default Trips;
